import schema from './comp/schema'










const Page =(props) => {


const  generateData = props.generateData

    // to keep track of your input

    const [comment,setComment] = useState({
      title: '',
        body:""

    })

    // to keep track of our errors

    const [errors,setErrors] = useState({
        title: '',
        body:""

    })

    const changeinput = (event)=> {
        const {value,name} = event.target
        setComment({...comment,[name]:value})
    }

    const submit =(event) => {
    event.preventDefault()
        generateData(comment)
        setComment({
            title: '',
            body:''

        })
    }


    return (
      <form onSubmit={submit}>
          <input name="title" onChange={changeinput} value={comment.title} placeholder={'write title'} />
          <textarea name={'body'} onChange={changeinput} value={comment.body} placeholder={'in 20 words or more expalin it to me'} />
          <button>submit</button>
      </form>
    )


}
export default  Page
